import {useState} from 'react';
import Header from './Header';
import style from './Form.module.css';

function Form () {
  const [nome, setNome] = useState('');
  const [email, setEmail] = useState('');
  const [profissao, setProfissao] = useState('');
  const [listUser, setListUser] = useState([]);
  let id = 1;

  function CadastrarUsuario(e) {
    e.preventDefault()
    const add = listUser.push({nome:nome, email:email, profissao:profissao});
    setListUser(add);
    id ++;
  }

  return (
    <div className={style.cadastroUsuario}>
      <Header/>
      <form onSubmit={CadastrarUsuario}>
        <div>
          <input onChange={(e) => {setNome(e.target.value)}} type="text" placeholder="Digite o seu nome"></input>
        </div>
        <div>
          <input onChange={(e) => {setEmail(e.target.value)}} type="mail" placeholder="Digite o seu e-mail"></input>
        </div>
        <div>
          <input onChange={(e) => {setProfissao(e.target.value)}} type="text" placeholder="Digite o seu nome"></input>
        </div>
        <div>
          <input type="submit" value="Cadastrar">Cadastrar Usuário</input>
        </div>
      </form>

    </div>
    );
  }


export default Form;
