import {useState} from 'react';
function Form () {
  function CadastrarUsuario (e) {
    e.preventDefault();
    let id = 1;
    id = id.length + 1;
    setListUser = ([...ListUser, {nome: nome, email: email, profissao: profissao}]);
  }

  const [nome, setNome] = useState();
  const [email, setEmail] = useState();
  const [profissao, setProfissao] = useState();
  const [ListUser, setListUser] = useState([]);

  return(
    <>
      <h1>Trabalhadores</h1>
      <form>
        <div>
          <input onChange={(e => setNome(e.target.value))} type="text" placeholder="Digite o nome completo"/>
        </div>

        <div>
          <input onChange={(e=> setEmail(e.target.value))} type="email" placeholder="Digite seu e-mail"/>
        </div>

        <div>
          <input oncChange={(e=>setProfissao(e.target.value))} type="text" placeholder="Digite sua profissão"/>
        </div>

        <div>
          <input type="submit" value="Adicionar Usuário"/>
        </div>
      </form>           
    </>
  );
}

export default Form;
