import { useState } from 'react';
import Header from './Header';
import style from './Form.module.css'

function Form() {
  
  function CadastraUsuario(e) {
    e.preventDefault();
    let id = 1;
    id = id.length + 1
    setListUser([...listUser, {id: id, nome: nome, email: email, profissao: profissao }])
  }

  const [nome, setNome] = useState();
  const [email, setEmail] = useState();
  const [profissao, setProfissao] = useState();
  const [listUser, setListUser] = useState([]);

  return (
    <div>
      <div className={style.cadastroUsuario}> 
        <Header/>    
        <form>        
          <div>
            <input onChange={(e) => setNome(e.target.value)} type="text" placeholder="Digite o seu nome" />
          </div>

          <div>
            <input onChange={(e) => setEmail(e.target.value)} type="mail" placeholder="Digite o seu e-mail" />
          </div>

          <div>
            <input onChange={(e) => setProfissao(e.target.value)} type="text" placeholder="Digite a sua profissão" />
          </div>

          <div className={style.botao}>
            <input onClick={CadastraUsuario} type="submit" value="Cadastrar" />
          </div>
        </form>    
      </div>

      <div className={style.listaCadastrados}>
        {listUser.map(user => (
          <ul>
            <div className={style.alinharTexto}>
              <p>Nome:</p> <li eky={user.id}>{user.nome} </li>
            </div>
            <div className={style.alinharTexto}>
              <p>E-mail:</p><li eky={user.id}>{user.email}</li>
            </div>
            <div className={style.alinharTexto}>
            <p>Profissão:</p><li eky={user.id}>{user.profissao}</li>
            </div>
          </ul>
        ))}
      </div>
    </div>
  );
}

export default Form;
