import {useState} from 'react';
import Header from './Header';
import style from './Form.module.css';

function Form () {
  const [nome, setNome] = useState('');
  const [email, setEmail] = useState('');
  const [profissao, setProfissao] = useState('');
  const [listaCadastrados, setListaCadastrados] = useState([]);
  let id = 1;

  function CadastrarUsuario(e) {
    e.preventDefault();
    const adicionar = listaCadastrados.push({nome:nome, email:email, profissao:profissao});
    setListaCadastrados(adicionar)
    id ++;
  }

  return (
    <div className={style.cadastroUsuario}>
      <Header/>
      <form onSubmit={CadastrarUsuario}>
        <div>
          <input id="nome" label="nome" placeholder="Digite seu nome" value={nome} onChange={(e)=>{setNome(e.target.value)}}/>
        </div>
        <div>
          <input id="email" label="email" placeholder="Digite seu e-mail" value={email} onChange={(e)=>{setEmail(e.target.value)}}/>
        </div>
        <div>
          <input id="profissao" label="profissao" placeholder="Digite sua profissão" value={profissao} onChange={(e)=>{setProfissao(e.target.value)}}/>
        </div>
        <div>
          <input type="submit" value="Cadastrar">Cadastrar Usuário</input>
        </div>
      </form>

    </div>
    );
  }


export default Form;
