import {useState} from 'react';
import style from './Form.module.css'
function Form () {
  function CadastrarUsuario (e) {
    e.preventDefault();
    let id = 1;
    id = id.length + 1;
    setListUser = ([...ListUser, {nome: nome, email: email, profissao: profissao}]);
  }

  const [nome, setNome] = useState();
  const [email, setEmail] = useState();
  const [profissao, setProfissao] = useState();
  const [ListUser, setListUser] = useState([]);

  return(
    <div className={style.cadastroUsuario}>
      <Header/>
      <form>
        <div>
          <input onChange={(e => setNome(e.target.value))} type="text" placeholder="Digite o nome completo"/>
        </div>

        <div>
          <input onChange={(e=> setEmail(e.target.value))} type="email" placeholder="Digite seu e-mail"/>
        </div>

        <div>
          <input onChange={(e=>setProfissao(e.target.value))} type="text" placeholder="Digite sua profissão"/>
        </div>

        <div>
          <input onClick={CadastrarUsuario} type="submit" value="Adicionar Usuário"/>
        </div>
      </form>       

      <div>
        
      </div>    
    </div>
  );
}

export default Form;
