import {useState} from 'react';
import Input from './Input';
import Header from './Header';
import style from './Form.module.css';

function Form () {
  const initialValues = {nome: '', email: '', profissao: ''};
  const useForm = () => {
    const [form, setForm] = useState(initialValues);

    const setInput = (e) => {
      setForm( form => {
        return ({ ...form, ...e });
      })
  }
}

  return (
    <div className={style.cadastroUsuario}>
      <Header/>
      <form>
        <div className="formGroup">
          <Input nome="nome" placeholder="Digite aqui seu nome" label="nome" onChange={e=> setInput({nome:e.target.value})}/>
        </div>

        <div className="formGroup">
          <Input email="email" placeholder="Digite aqui seu email" label="email" onChange={e=>setInput({email:e.target.value})}/>
        </div>

        <div className="formGroup">
          <Input profissao="profissao" placeholder="Digite aqui sua profissao" label="profissao" onChange={e=>setInput({profissao:e.target.value})}/>
        </div>

        <div className="formGroup">
          <button className="button"> Cadastrar</button>

        </div>

      </form>

    </div>
    );
}

export default Form;
